# database.py# -*- coding: utf-8 -*-import psycopg2from config import DB_CONFIGdef init_db():    conn = psycopg2.connect(**DB_CONFIG)    cursor = conn.cursor()    cursor.execute('''        CREATE TABLE IF NOT EXISTS trades (            id SERIAL PRIMARY KEY,            bot_id TEXT,            exec_id TEXT UNIQUE,            symbol TEXT,            side TEXT,            qty REAL,            price REAL,            profit REAL,            timestamp INTEGER        )    ''')    conn.commit()    conn.close()def save_trade(trade_data):    conn = psycopg2.connect(**DB_CONFIG)    cursor = conn.cursor()    cursor.execute(        "SELECT 1 FROM trades WHERE exec_id = %s",        (trade_data['exec_id'],)    )    if cursor.fetchone():        conn.close()        return    cursor.execute('''        INSERT INTO trades (            bot_id, exec_id, symbol, side, qty, price, profit, timestamp        ) VALUES (%s, %s, %s, %s, %s, %s, %s, %s)    ''', (        trade_data['bot_id'],        trade_data['exec_id'],        trade_data['symbol'],        trade_data['side'],        trade_data['qty'],        trade_data['price'],        trade_data['profit'],        trade_data['timestamp']    ))    conn.commit()    conn.close()